 <?php    class Controller_Item extends Controller	{		function __construct()		{			if (!self::db_check())			{				return;			}						View::instance() -> set_tpl_dir('controllers\item');		}				public function action_index($controllerArguments) 		{			$view = View::instance();						$model = new Model_Item();						$view -> item_info = $model -> get_info((int)$controllerArguments['entry']);						$this -> display('index');						return;		}    }